import React from 'react'

class DogCard extends React.Component {
    render(){
        let dogToRender = this.props.dog
        let dog = this.props.dogs.map((dog) => {
            return(
                <>
                    <img src={dog.image} />
                    <h2>{dog.name}</h2>
                    <button>
                        {dog.isGoodDog
                        ? 
                        "Good Dog!"
                        : 
                        "Bad Dog!"
                        }
                    </button>
                </>
            )
        })

        dogInfoToRender = () => {
            this.dogList.map((dogPOJO) => {
              if (dogPOJO.name === this.dogToRender()){
                return dogPOJO
              }
            })
          }

        if (dogToRender === dog.name){
            return dog
        }

        return(    
            dog
        )
    }
}

export default DogCard
