import React from 'react'

class DogCard extends React.Component {
    render(){
        let dogToRender = this.props.dog
        let dog = this.props.dogs.map((dogPOJO) => {
            if (dogPOJO.name === dogToRender){
                return dogPOJO
            }
        })

        return(    
            <>
                <img src={dog.image} />
                <h2>{dog.name}</h2>
                <button>
                    {dog.isGoodDog
                    ? 
                    "Good Dog!"
                    : 
                    "Bad Dog!"
                    }
                </button>
            </>
        )
    }
}

export default DogCard
