import React from 'react';
import './App.css';
import DogContainer from './DogContainer'
import DogCard from './DogCard'

class App extends React.Component {
  state = {
    dogList: [],
    dogToBeRendered: ""
  }

  componentDidMount(){
    fetch("http://localhost:3000/pups")
    .then(r => r.json())
    .then((dogs) => {
      this.setState({
        dogList: dogs
      })
    })
  }

  dogToRender = (dogNameFromChild) => {
    this.setState({
      dogToBeRendered: dogNameFromChild.id
    })
  }

  render(){ 
    let {dogList, dogToBeRendered} = this.state
    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <div id="dog-bar">
          <DogContainer
            dogs={dogList}
            dogToRender={this.dogToRender}
          />
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            <DogCard
              dogs={dogList}
              dog={dogToBeRendered}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
