import React from 'react';
import './App.css';
import DogContainer from './DogContainer'
import DogCard from './DogCard'

class App extends React.Component {
  state = {
    dogList: [],
    dogToRender: ""
  }

  componentDidMount(){
    fetch("http://localhost:3000/pups")
    .then(r => r.json())
    .then((dogs) => {
      this.setState({
        dogList: dogs
      })
    })
  }

  renderDogInformation = (dogNameFromChild) => {
    this.setState((prevState) => {
      dogToRender: [...prevState, ]
    })
  }

  render(){ 
    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <div id="dog-bar">
          <DogContainer
            dogs={this.state.dogList}
          />
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {/* <DogCard
            /> */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
